import React, { PureComponent, Fragment } from 'react'
import Header3 from './public/header3'
import Moment from 'moment'
import api from './public/api'
import Alert from 'react-s-alert'
import { Redirect } from 'react-router-dom'

class Login extends PureComponent {
    constructor(props) {
        super(props)

        this.state = {
            email:"",
            login_as:"",
            password:"",
            stud_login : false,
            profile:{},
            instruct_login:false
        }
    }
    componentDidMount(){
        if(localStorage.getItem("activatemail") !==null || localStorage.getItem("activatemail") !== undefined){
            Alert.success(localStorage.getItem("activatemail") , {
                position: 'top',
                effect: 'scale',
                onShow: function () {
                },
                beep: false,
                timeout: 5000,
                offset: 10
              })
            localStorage.removeItem("activatemail")
        }
    }
    Login = (e) =>{
        e.preventDefault();
        let {email,password,login_as,profile} = this.state;
        console.log(login_as)
        if(email !== "" && password !== "" && login_as !=="0"){
            api.post("/auth/login",{
                email:email,
                password:password,
                groupID:parseInt(login_as)
              }).then(res=>{
                if(res.data.success==true && res.data.responseType == 'successful'){
                    Alert.success('Login Successful', {
                        position: 'top',
                        effect: 'scale',
                        onShow: function () {
                        },
                        beep: false,
                        timeout: 3000,
                        offset: 10
                      });
                     let userprofile={
                          "token":res.data.authToken,
                          "isloggedin":true
                      }
                    //   console.log(userprofile)
                    localStorage.setItem("userprofile",JSON.stringify(userprofile))
                    if((res.data.user.group_id == 1 || res.data.user.group_id == "1") &&(login_as =="1")){
                        this.setState({stud_login: true})
                        this.setState({instruct_login:false})
                    }

                    else if((res.data.user.group_id == 2 || res.data.user.group_id == "2") &&(login_as == "2")){
                        this.setState({stud_login :false})
                        this.setState({instruct_login:true})
                    }
                    else{
                        console.log("invalid group id")
                    }
                    //   this.setState({profile:userprofile})
                }
                else{
                    Alert.error(res.data.message, {
                        position: 'top',
                        effect: 'scale',
                        onShow: function () {
                        },
                        beep: false,
                        timeout: 3000,
                        width:'50px',
                        // offset: 10
                      });
                }
            })
        }

        else{
            Alert.error('field must not be empty', {
                position: 'top',
                effect: 'scale',
                onShow: function () {
                },
                beep: false,
                timeout: 3000,
                width:'50px',
                // offset: 10
              });
            this.setState({instruct_login :false})
            this.setState({stud_login:false})
        }
    }
    render() {
       const {stud_login,instruct_login} = this.state;
        let dated = Moment().format('YYYY')

        if(stud_login){
            return <Redirect to="/student-dashboard" />
        }
        if(instruct_login){
            return <Redirect to="/instructor-dashboard" />
        }
        return (
            <Fragment>
                <div className="preloader">
        <div className="sk-double-bounce">
            <div className="sk-child sk-double-bounce1"></div>
            <div className="sk-child sk-double-bounce2"></div>
        </div>
    </div>

    {/* <!-- Header Layout   --> */}
    <div className="mdk-header-layout js-mdk-header-layout">

        {/* <!-- Header   --> */}

        <Header3/>

        {/* <!-- // END Header   --> */}

        {/* <!-- Header Layout Content   --> */}
        <div className="mdk-header-layout__content page-content pb-0">

            <div className="bg-gradient-primary py-32pt">
                <div className="container d-flex flex-column flex-md-row align-items-center text-center text-md-left">
                    <img src="./assets/images/illustration/student/128/white.svg" className="mr-md-32pt mb-32pt mb-md-0" alt="student"/>
                    <div className="flex mb-32pt mb-md-0">
                        <h1 className="text-white mb-0">Sign In</h1>
                        <p className="lead measure-lead text-white-50">Account Management</p>
                    </div>
                    <a href="/signup" className="btn btn-outline-white flex-column">
                        Don't have an account?
                        <span className="btn__secondary-text">Sign up Today!</span>
                    </a>
                </div>
            </div>
            <div className="bg-white pt-32pt pt-sm-64pt pb-32pt">
                <div className="container page__container">
                    <form action="student-dashboard" className="col-md-5 p-0 mx-auto">
                        <div className="form-group">
                            <label for="email">Email:</label>
                            <input id="email" type="text" className="form-control" placeholder="Your email address ..." onChange={e => this.setState({ email: e.target.value })}/>
                        </div>
                        <div className="form-group">
                            <label for="text">Login as:</label>
                            <select id="text" type="text" className="form-control" onChange={e => this.setState({ login_as: e.target.value })} >
                                <option value= "0">select an option...</option>
                                <option value= "1">student</option>
                                <option value= "2">instructor</option>
                                {/* <option value= "3">Admin</option> */}
                            </select>
                        </div>
                        <div className="form-group">
                            <label for="password">Password:</label>
                            <input id="password" type="password" className="form-control" placeholder="Your first and last name ..." onChange={e => this.setState({ password: e.target.value })}/>
                        </div>
                        <div className="form-group" style={{display:"inline-flex"}}>
                                <div className="col-md-4 col-sm-4 col-lg-4">
                                    <a href="/pricing" className="medium">Sign up</a>
                                </div>
                                <div className="col-md-2 col-sm-4 col-lg-2">&nbsp;</div>
                                <div className="col-md-8 col-sm-4 col-lg-8">
                                    <a href="/reset-password" className="small">Forgot your password?</a>
                                </div>
                        </div>
                        <div className="text-center">
                            <button className="btn btn-lg btn-accent" onClick={this.Login}>Login</button>
                        </div>
                    </form>
                </div>
            </div>
            <div className="page-separator m-0">
                <div className="page-separator__text">or sign-in with</div>
                <div className="page-separator__bg-top bg-white"></div>
            </div>
            <div className="bg-body pt-32pt pb-32pt pb-md-64pt text-center">
                <div className="container page__container">
                    <a href="/student-dashboard" className="btn btn-lg btn-secondary btn-block-xs">Facebook</a>
                    <a href="/student-dashboard" className="btn btn-lg btn-secondary btn-block-xs">Twitter</a>
                    <a href="/student-dashboard" className="btn btn-lg btn-secondary btn-block-xs">Google+</a>
                </div>
            </div>


        </div>
        {/* <!-- // END Header Layout Content   --> */}

    </div>
    {/* <!-- // END Header Layout   --> */}

    <div className="navbar navbar-expand-sm navbar-mini navbar-dark fixed-bottom bg-dark d-none d-md-flex p-0" id="demo-navbar">

    </div>
     </Fragment>
        )
    }
}

export default Login
