import React, { PureComponent, Fragment } from 'react'
import { Redirect } from 'react-router-dom'
import api from './api'

class Studentheaderlayout extends PureComponent {
    constructor(props) {
        super(props)

        this.state = {
            loading:false,
            redirect: false,
            userprofile:[],
            userdata:[],
            totalmonthbalance:"",
            totalbalance:"", 
            totalcourse:"",
            courseinfos:[]
        }
    }
    componentWillMount(){
        const auth= localStorage.getItem('userprofile')

        if(auth == undefined || auth ==null){
            this.setState({redirect:true})
        }
        else{
            api.post("/auth/me",{token:JSON.parse(auth).token}).then(res=>{
                // console.log("userprofile",res.data)
                if(res.data.success==true){

                    this.setState({userprofile:res.data.user})

                }
            })

        }
    }
    render() {
        const {redirect,userprofile} = this.state
        if(redirect){
            return <Redirect to="/login"/>
        }
        return (
            <Fragment>
                <div className="bg-gradient-primary border-bottom-white py-32pt">
                            <div className="container d-flex flex-column flex-md-row align-items-center text-center text-md-left">
                                <img src="./assets/images/illustration/student/128/white.svg" width="104" className="mr-md-32pt mb-32pt mb-md-0" alt="student"/>
                                <div className="flex mb-32pt mb-md-0">
                                    <h2 className="text-white mb-0">userprofile</h2>
                                    <p className="lead text-white-50 d-flex align-items-center">Student <span className="ml-16pt d-flex align-items-center"><i className="material-icons icon-16pt mr-4pt">opacity</i> 2,300 IQ</span></p>
                                </div>
                                <a href="/student-edit-account" className="btn btn-outline-white">Edit account</a>
                            </div>
                        </div>

                        <div className="navbar navbar-expand-sm navbar-dark-white bg-gradient-primary p-sm-0 ">
                            <div className="container page__container">

                                {/* <!-- Navbar toggler   --> */}
                                <button className="navbar-toggler ml-n16pt" type="button" data-toggle="collapse" data-target="#navbar-submenu2">
                                    <span className="material-icons">people_outline</span>
                                </button>

                                <div className="collapse navbar-collapse" id="navbar-submenu2">
                                    <div className="navbar-collapse__content pb-16pt pb-sm-0">
                                        <ul className="nav navbar-nav">

                                            <li className="nav-item active">
                                                <a href="/student-dashboard" className="nav-link">Dashboard</a>
                                            </li>
                                            <li className="nav-item">
                                                <a href="/student-my-courses" className="nav-link">Courses</a>
                                            </li>
                                            <li className="nav-item">
                                                <a href="/student-discussions" className="nav-link">Discussions</a>
                                            </li>

                                        </ul>
                                        <ul className="nav navbar-nav ml-auto">
                                            <li className="nav-item">
                                                <a href="/student-profile" className="nav-link">Profile</a>
                                            </li>
                                        </ul>
                                    </div>
                                </div>
                            </div>
                        </div>
            </Fragment>
        )
    }
}

export default Studentheaderlayout
